	public String longestCommonPrefix(String[] strs) {

		if (strs == null)
			return null;
        if(strs.length==1) return strs[0]; //Ö»ÓÐÒ»¸ö×Ö·û´®µÄÊ±ºò£¬·µ»Øµ±Ç°×Ö·û´®¡£
		Node root = buildTree(strs);
		String s = longest(root,strs.length);
		return s.substring(1);

	}

	public Node buildTree(String[] strs) {
		Node root = new Node('r');
		root.count=Integer.MAX_VALUE; //±ÜÃâÎÞ·¨¼ÌÐø¼ÆËãµÄÎÊÌâ,Ö®Ç°ÓÃ10¾ÍÌ«Ð¡ÁË£¬¿¼ÂÇ´óÊýÎÊÌâ£¡£¡£¡
		for (int i = 0; i < strs.length; i++) {
			Node now = root;
			for (int j = 0; j < strs[i].length(); j++) {
				char c = strs[i].charAt(j);
//				System.out.println("c=" + c);
				if (now.next.get(c) == null) {
					Node n = new Node(c);
					now.next.put(c, n);
					now = n;
				} else {
					//Ë³ÐòÅª·´ÁË£¬Ó¦¸ÃÏÈnextÔÙ++µÄ! ²»È»¾Í¼Ó´íµØ·½ÁË£¡
					now = now.next.get(c);
					now.count++;
//					System.out.println(now.val+" count="+now.count);
					
				}
			}
		}
		return root; // Íü¼ÇreturnÁË
	}

	public String longest(Node root,int k) {
		String longest = "";
//		System.out.println(root.val + " " + root.count);
		if (root==null || root.count < k )  //²»ÄÜÖ±½ÓÅÅ³ýÒ¶×Ó£¡£¡ ÒòÎªÓÐ¿ÉÄÜÓÐÁ½¸öÍêÈ«ÏàÍ¬µÄ×Ö·û´®£¡
			return null;
		StringBuilder sb = new StringBuilder();
		sb.append(root.val);

		Iterator<Entry<Character, Node>> itr = root.next.entrySet().iterator();
		while (itr.hasNext()) {
			
			Map.Entry<Character, Node> en = (Map.Entry<Character, Node>) itr.next();
			Node n = (Node) en.getValue();
			String now = longest(n,k);
			if (now != null && now.length() > longest.length()) { // String
																	// ÊÇlength()!!!
				longest = now;
			}
		}
		sb.append(longest);

		return sb.toString();
	}

	public class Node {
		HashMap<Character, Node> next;
		char val;
		int count;  //·Ç³£ÖØÒª£¬ÓÃÀ´¼ÇÂ¼±»±éÀú¹ý¼¸´Î£¬<2±íÊ¾¾ø¶Ô²»ÊÇ¹«¹²×Ó´®ÀïÃæµÄ

		Node(char v) {
			val = v;
			count = 1;
			next = new HashMap<Character, Node>();
		}
	}
